package com.pagingimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport androidx.lifecycle.ViewModelProviderimport androidx.recyclerview.widget.LinearLayoutManagerimport androidx.recyclerview.widget.RecyclerViewimport java.util.HashMapclass MainActivity : AppCompatActivity() {    private lateinit var mainActivityViewModel: MainActivityViewModel    private lateinit var recyclerView: RecyclerView    private lateinit var postAdapter: UsersProfilePagedListAdapter    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        mainActivityViewModel = ViewModelProvider(this).get(MainActivityViewModel::class.java)        recyclerView = findViewById(R.id.recyclerView)        recyclerView.hasFixedSize()        recyclerView.layoutManager = LinearLayoutManager(this, LinearLayoutManager.VERTICAL, false)        val map: HashMap<String, String> = HashMap<String, String>()        postAdapter = UsersProfilePagedListAdapter(this)        mainActivityViewModel.requestOrderByPagedList(map, this).observe(this, {            postAdapter.submitList(it)            postAdapter.notifyDataSetChanged()        })    }}